import React, { Component } from "react";
import styled from 'styled-components';
// icons
import StoreOutlinedIcon from '@material-ui/icons/StoreOutlinedIcon';
import MoodOutlinedIcon from '@material-ui/icons/MoodOutlined';
import FavoriteBorderOutlinedIcon from '@material-ui/icons/FavoriteBorderOutlined';
import MailOutlinedIcon from '@material-ui/icons/MailOutlined';
import SettingsOutlinedIcon from '@material-ui/icons/SettingsOutlined';
const Wrapper = styled.section`
    display: flex;
    flex-direction: column;
    width: 250px;
    min-height: 100vh;
    background: #160754;
    color: #a79dd4;
`;
const Logo = styled.div`
    margin: 1rem auto;
    color: #fff;
    font-size: 2rem;
    font-weight: bold;
    & span {
        color: #6a43f7;
        font-style: italic;
    }
`;
const Menu = styled.ul`
    list-style: none;
    padding: 0;
    text-align: left;
    & .active {
        background: #6a43f7;
        color: #d9cfff;
    }
`;
const MenuItem = styled.li`
    padding: 0.75rem 2rem;
    &:hover {
        cursor: pointer;
    }
`;

class Sidebar extends Component {
    render() {
        return (
            <Wrapper>
                <Logo>grow<span>flow</span></Logo>
                <Menu>
                    <MenuItem className="active"><MoodOutlinedIcon /> Employees</MenuItem>
                    <MenuItem><MailOutlinedIcon/> Messages</MenuItem>
                    <MenuItem><StoreOutlinedIcon/> Store</MenuItem>
                    <MenuItem><FavoriteBorderOutlinedIcon/> Favorites</MenuItem>
                    <MenuItem><SettingsOutlinedIcon/> Settings</MenuItem>
                </Menu>
            </Wrapper>
        )
    }
}

export default Sidebar
