import React, { Component } from "react";
import styled from 'styled-components';
import {Pagination} from '@material-ui/lab';

// redux
import { connect } from "react-redux";
import { getEmployees } from "../redux/actions/dataActions";

const Wrapper = styled.section`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    width: 100%;
    height: auto;
    padding: 1rem;
    background: #fff;
`;
const Actions = styled.div`
    padding: 1rem;
    display: flex;
    justify-content: space-between;
`;
const SearchInput = styled.input`
    width: 250px;
    padding: 0 0.5rem;
    outline: none;
    border: 1px solid #bdbdbd;
`;
const Table = styled.div``;
const Row = styled.li`
    display: flex;
    width: 100%;
    border-bottom: 1px solid #f0f0f0;
    justify-content: space-around;
    padding: 1rem;
`;
const RowHeader = styled.li`
display: flex;
width: 100%;
border-bottom: 1px solid #f0f0f0;
background: #f0f0f0;
padding: 1rem;
font-weight: bold;
`;
const Col = styled.div`
    flex: 1;
`;
const Paginate = styled.ul`
    display: flex;
    list-style: none;
    padding: 0;
    & li {
        margin: 0 1rem;
        cursor: pointer;
    }
`;

class Data extends Component {
    constructor() {
        super();
        this.state = {
          currentPage: 1,
          itemsPerPage: 9
        };
        this.handleChange = this.handleChange.bind(this);
      }
    componentDidMount() {
        this.props.getEmployees();
    }
    handleChange(event) {
        this.setState({
          currentPage: Number(event.target.id)
        });
      }
    render() {
        const { employees, loading } = this.props.data;
        const { currentPage, itemsPerPage } = this.state;
        const count = Math.ceil(employees.length / itemsPerPage);



        return (
            <Wrapper>
               <Actions>
                   <SearchInput type="text" placeholder="Search employee"></SearchInput>
               </Actions>
               <Table>
                   <RowHeader>
                       <Col>btn</Col>
                       <Col>First Name</Col>
                       <Col>Last Name</Col>
                       <Col>Email Address</Col>
                       <Col>Phone Number</Col>
                       <Col>Role</Col>
                       <Col>Action</Col>
                   </RowHeader>
                    {employees.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage).map((employee) =>
            <Row key={employee.id}>
                <Col>btn</Col>
                <Col>{employee.firstName}</Col>
                <Col>{employee.lastName}</Col>
                <Col>{employee.email}</Col>
                <Col>{employee.telephone}</Col>
                <Col>{employee.role}</Col>
                <Col>View</Col>
            </Row>
        )}
                    <Paginate>
                        <Pagination count={count} page={currentPage} variant="outlined" shape="rounded" onChange={this.handleChange} />
                    </Paginate>
               </Table>
            </Wrapper>
        )
    }
}

const mapStateToProps = state => ({
    data: state.data
})

const mapActionsToProps = {
    getEmployees
}

export default connect(mapStateToProps, mapActionsToProps)(Data);
