import React, { Component } from "react";
import styled from 'styled-components';

// redux
import { connect } from "react-redux";
import { getEmployees } from "../redux/actions/dataActions";

const Wrapper = styled.section`
    display: flex;
    justify-content: space-between;
    width: 100%;
    height: 100px;
    padding: 1rem;
    background: #fff;
`;
const Actions = styled.div`
    padding: 1rem;
    display: flex;
    justify-content: space-between;
`;
const SearchInput = styled.input`
    width: 250px;
    padding: 0 0.5rem;
    outline: none;
    border: 1px solid #bdbdbd;
`;
const Table = styled.div``;
const Row = styled.li`
    display: flex;
`;
const Col = styled.div`
    
`;

class Data extends Component {
    componentDidMount() {
        this.getEmployees();
    }
    render() {
        const { employees, loading } = this.props.data;

         // filter employees by createdAt
        const orderedPosts = employees.sort(function (a, b) {
            var c = new Date(a.createdAt);
            var d = new Date(b.createdAt);
            return d - c;
        });
    
        let employeesMarkup = loading ? null : employees.length === 0 ? (
            <div>No employees to display.</div>
        ) : (employees.map((employee) =>
            <Row key={employee.id}>
                <Col></Col>
            </Row>
        )
        );

        return (
            <Wrapper>
               <Actions>
                   <SearchInput type="text" placeholder="Search employee"></SearchInput>
               </Actions>
               <Table>
                    {employeesMarkup}
               </Table>
            </Wrapper>
        )
    }
}

const mapStateToProps = state => ({
    data: state.data
})

const mapActionsToProps = {
    getEmployees
}

export default connect(mapStateToProps, mapActionsToProps)(Data);
