import {
    LOADING_DATA,
    SET_EMPLOYEES,
    CREATE_EMPLOYEE,
    SET_ERRORS,
    CLEAR_ERRORS,
    LOADING_UI
  } from "../types";
import axios from "axios";
import { apiKey, apiUser } from "../apiObject";

const params = {
	ApiKey: apiKey,
	ApiUser: apiUser,
};
  
// get all employees
export const getEmployees = () => async (dispatch) => {
    dispatch({ type: LOADING_DATA });
    try {
        const res = await axios.get("/employee", { params });
        dispatch({
            type: SET_EMPLOYEES,
            payload: res.data,
        });
    }
    catch (e) {
        dispatch({
            type: SET_EMPLOYEES,
            payload: [],
        });
    }
};

// create an employee
export const createEmployee = (newEmployee) => async (dispatch) => {
    dispatch({ type: LOADING_UI });
    try {
        const res = await axios.post("/employee", newEmployee, { params });
        dispatch({
          type: CREATE_EMPLOYEE,
          payload: res.data,
        });
        dispatch(clearErrors());
      }
    catch (e) {
        dispatch({
            type: SET_ERRORS,
            payload: e.response.data,
        });
    };
};

// clear UI errors
export const clearErrors = () => (dispatch) => {
    dispatch({ type: CLEAR_ERRORS });
};
