import React, { Component } from "react";
import styled from 'styled-components';

// redux
import { connect } from "react-redux";
import { getEmployees } from "../redux/actions/dataActions";

const Wrapper = styled.section`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    width: 100%;
    height: auto;
    padding: 1rem;
    background: #fff;
`;
const Actions = styled.div`
    padding: 1rem;
    display: flex;
    justify-content: space-between;
`;
const SearchInput = styled.input`
    width: 250px;
    padding: 0 0.5rem;
    outline: none;
    border: 1px solid #bdbdbd;
`;
const Table = styled.div``;
const Row = styled.li`
    display: flex;
    width: 100%;
    border-bottom: 1px solid #f0f0f0;
    justify-content: space-around;
    padding: 1rem;
`;
const RowHeader = styled.li`
display: flex;
width: 100%;
border-bottom: 1px solid #f0f0f0;
background: #f0f0f0;
padding: 1rem;
font-weight: bold;
`;
const Col = styled.div`
    flex: 1;
`;
const Paginate = styled.ul`
    display: flex;
    list-style: none;
    padding: 0;
    & li {
        margin: 0 1rem;
        cursor: pointer;
    }
`;

class Data extends Component {
    constructor() {
        super();
        this.state = {
          currentPage: 1,
          dataPerPage: 10
        };
        this.handleClick = this.handleClick.bind(this);
      }
    componentDidMount() {
        this.props.getEmployees();
    }
    handleClick(event) {
        this.setState({
          currentPage: Number(event.target.id)
        });
      }
    render() {
        const { employees, loading } = this.props.data;
        const { currentPage, dataPerPage } = this.state;

        const indexOfLastData = currentPage * dataPerPage;
        const indexOfFirstData = indexOfLastData - dataPerPage;
        const currentData = employees.slice(indexOfFirstData, indexOfLastData);

        let renderData = loading ? <div>Loading</div> : currentData.length === 0 ? (
            <div>No employees to display.</div>
        ) : (currentData.map((employee) =>
            <Row key={employee.id}>
                <Col>btn</Col>
                <Col>{employee.firstName}</Col>
                <Col>{employee.lastName}</Col>
                <Col>{employee.email}</Col>
                <Col>{employee.telephone}</Col>
                <Col>{employee.role}</Col>
                <Col>View</Col>
            </Row>
        )
        );

        // Logic for displaying page numbers
        const pageNumbers = [];
        for (let i = 1; i <= Math.ceil(employees.length / dataPerPage); i++) {
        pageNumbers.push(i);
        }

        const renderPageNumbers = pageNumbers.map(number => {
        return (
            <li
            key={number}
            id={number}
            onClick={this.handleClick}
            >
            {number}
            </li>
        );
        });

        return (
            <Wrapper>
               <Actions>
                   <SearchInput type="text" placeholder="Search employee"></SearchInput>
               </Actions>
               <Table>
                   <RowHeader>
                       <Col>btn</Col>
                       <Col>First Name</Col>
                       <Col>Last Name</Col>
                       <Col>Email Address</Col>
                       <Col>Phone Number</Col>
                       <Col>Role</Col>
                       <Col>Action</Col>
                   </RowHeader>
                    {renderData}
                    <Paginate>
                        {renderPageNumbers}
                    </Paginate>
               </Table>
            </Wrapper>
        )
    }
}

const mapStateToProps = state => ({
    data: state.data
})

const mapActionsToProps = {
    getEmployees
}

export default connect(mapStateToProps, mapActionsToProps)(Data);
