import React, { Component } from "react";
import styled from 'styled-components';
import { Pagination } from '@material-ui/lab';
import CircularProgress from '@material-ui/core/CircularProgress';
// component
import EmployeeModal from './EmployeeModal';
// redux
import { connect } from "react-redux";
import { getEmployees } from "../redux/actions/dataActions";

const Wrapper = styled.section`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    width: 100%;
    height: auto;
    padding: 1rem;
    background: #fff;
`;
const Actions = styled.div`
    padding: 1rem;
    display: flex;
    justify-content: space-between;
`;
const SearchInput = styled.input`
    width: 250px;
    padding: 0 0.5rem;
    outline: none;
    border: 1px solid #bdbdbd;
`;
const Table = styled.div``;
const Row = styled.li`
    display: flex;
    width: 100%;
    border-bottom: 1px solid #f0f0f0;
    justify-content: space-around;
    padding: 1rem;
`;
const RowHeader = styled.li`
    display: flex;
    width: 100%;
    border-bottom: 1px solid #f0f0f0;
    background: #f9f9f9;
    padding: 1rem;
    font-weight: bold;
`;
const Col = styled.div`
    flex: 1;
    & a {
        text-decoration: none;
        color: #6a43f7;
    }
`;
const Paginate = styled.div`
    display: flex;
    justify-content: flex-end;
    margin: 1rem 0;
    & li {
        margin: 0 0.5rem;
        cursor: pointer;
        & .MuiPaginationItem-page.Mui-selected {
            background: none;
            border: 1px solid #6a43f7;
            color: #6a43f7;
        }
    }
`;

class Data extends Component {
    constructor() {
        super();
        this.state = {
          currentPage: 6,
          itemsPerPage: 9
        };
        this.handleChange = this.handleChange.bind(this);
      }
    componentDidMount() {
        this.props.getEmployees();
    }
    handleChange(e, value) {
        this.setState({
          currentPage: value
        });
      }
    render() {
        const { employees, loading } = this.props.data;
        const { currentPage, itemsPerPage } = this.state;
        const count = Math.ceil(employees.length / itemsPerPage);

        let renderData = loading ? <Row><CircularProgress /></Row> : employees.length === 0 ? (
            <Row>No employees to display.</Row>
        ) : (employees.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage).map((employee) =>
            <Row key={employee.id}>
                <Col><input type="checkbox" /></Col>
                <Col>{employee.firstName}</Col>
                <Col>{employee.lastName}</Col>
                <Col>{employee.email}</Col>
                <Col>{employee.telephone}</Col>
                <Col>{employee.role}</Col>
                <Col><a href="">View</a></Col>
            </Row>
        )
        );


        return (
            <Wrapper>
               <Actions>
                   <SearchInput type="text" placeholder="Search employee"></SearchInput>
               </Actions>
               <Table>
                   <RowHeader>
                       <Col><input type="checkbox" /></Col>
                       <Col>First Name</Col>
                       <Col>Last Name</Col>
                       <Col>Email Address</Col>
                       <Col>Phone Number</Col>
                       <Col>Role</Col>
                       <Col>Action</Col>
                   </RowHeader>
                    {renderData}
                    <Paginate>
                        <Pagination count={count} page={currentPage} siblingCount={2} variant="outlined" shape="rounded" onChange={this.handleChange} />
                    </Paginate>
               </Table>
               <EmployeeModal />
            </Wrapper>
        )
    }
}

const mapStateToProps = state => ({
    data: state.data
})

const mapActionsToProps = {
    getEmployees
}

export default connect(mapStateToProps, mapActionsToProps)(Data);
