import React, { Component } from "react";
import styled from 'styled-components';
// icons
import CloseOutlinedIcon from '@material-ui/icons/CloseOutlined';
// redux
import { connect } from "react-redux";
import { getEmployees } from "../redux/actions/dataActions";

const Wrapper = styled.section`
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(0,0,0,0.5);
`;
const ModalCard = styled.div`
    width: 600px;
    height: 400px;
    background: #fff;
    border-radius: 2px;
    box-shadow: 0 0.15rem 1rem rgba(0,0,0,0.3);
`;
const Topbar = styled.div`
    width: 100%;
    border-bottom: 1px solid rgba(0,0,0,0.3);
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 1.5rem;
    & svg {
        cursor: pointer;
    }
`;
const Title = styled.h3`

`;
const Form = styled.form`
    padding: 2rem 1rem;
`;
const Row = styled.li`
    display: flex;
    width: 100%;
    justify-content: space-around;
`;
const Col = styled.div`
display: flex;
    flex: 1;
    flex-direction: column;
    & input {
        padding: 0.25rem 0.5rem;
        border: 1px solid rgba(0,0,0,0.3);
        border-radius: 5px;
    }
`;
const Buttons = styled.div`
    display: flex;
    justify-content: flex-end;
    padding: 1rem;
    border-top: 1px solid rgba(0,0,0,0.3);
    & .cancel {
        background: none;
        outline: none;
        border: 1px solid rgba(0,0,0,0.3);
        padding: 0.35rem 0.75rem;
        margin-right: 0.5rem;
    }
    & .create-new {
        outline: none;
        background: #6a43f7;
        color: #fff;
        border: none;
        padding: 0.35rem 0.75rem;
    }
`;

class Modal extends Component {
    state = {
        body: "",
        errors: {},
      };

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    };
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.createEmployee({ body: this.state.body, location: "explore" });
        this.props.clearErrors();
        this.setState({ body: "", errors: {} });
    };
      
    render() {
        const { errors } = this.state;
        return (
            <Wrapper>
                <ModalCard>
                    <Topbar>
                        <Title>Add New Employee</Title>
                        <CloseOutlinedIcon />
                    </Topbar>
                    <Form onSubmit={this.handleSubmit}>
                        <Row>
                            <Col>
                                <label for="fname">First Name</label>
                                <input type="text" id="fname" onChange={this.handleChange}></input>
                            </Col>
                            <Col>
                                <label for="lname">Last Name</label>
                                <input type="text" id="lname" onChange={this.handleChange}></input>
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <label for="email">Email</label>
                                <input type="text" id="email" onChange={this.handleChange}></input>
                            </Col>
                            <Col>
                                <label for="number">Phone Number</label>
                                <input type="text" id="number" onChange={this.handleChange}></input>
                            </Col>
                        </Row>
                        <Row>
                            <label>Role</label>
                        </Row>
                        <Row>
                            <input type="radio" name="role" id="driver" onChange={this.handleChange}></input>
                            <label for="driver">Driver</label>
                            <input type="radio" name="role" id="owner" onChange={this.handleChange}></input>
                            <label for="owner">Owner</label>
                            <input type="radio" name="role" id="other" onChange={this.handleChange}></input>
                            <label for="other">Other</label>
                        </Row>
                    </Form>
                    <Buttons>
                        <button className="cancel">
                            Cancel
                        </button>
                        <button type="submit" className="create-new">
                            Create New Employee
                        </button>
                    </Buttons>
                </ModalCard>
            </Wrapper>
        )
    }
}

const mapStateToProps = state => ({
    data: state.data
})

const mapActionsToProps = {
    getEmployees
}

export default connect(mapStateToProps, mapActionsToProps)(Modal);
