import React, { Component } from "react";
import styled from 'styled-components';
// icons
import CloseOutlinedIcon from '@material-ui/icons/CloseOutlined';
// redux
import { connect } from "react-redux";
import { getEmployees } from "../redux/actions/dataActions";

const Wrapper = styled.section`
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(0,0,0,0.5);
`;
const ModalCard = styled.div`
    width: 600px;
    height: 400px;
    background: #fff;
    border-radius: 2px;
    box-shadow: 0 0.15rem 1rem rgba(0,0,0,0.3);
`;
const Topbar = styled.div`
    width: 100%;
    border-bottom: 1px solid rgba(0,0,0,0.3);
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 1.5rem;
    & svg {
        cursor: pointer;
    }
`;
const Title = styled.h3`

`;
const Form = styled.form`
    padding: 1rem;
`;
const Buttons = styled.div`
    display: flex;
    justify-content: flex-end;
    padding: 1rem;
    border-top: 1px solid rgba(0,0,0,0.3);
    & .cancel {
        background: none;
        outline: none;
        border: 1px solid rgba(0,0,0,0.3);
        padding: 0.35rem 0.75rem;
        margin-right: 0.5rem;
    }
    & .create-new {
        outline: none;
        background: #6a43f7;
        color: #fff;
        border: none;
        padding: 0.35rem 0.75rem;
    }
`;

class Modal extends Component {
    state = {
        body: "",
        errors: {},
      };

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    };
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.createEmployee({ body: this.state.body, location: "explore" });
        this.props.clearErrors();
        this.setState({ body: "", errors: {} });
    };
      
    render() {
        const { errors } = this.state;
        return (
            <Wrapper>
                <ModalCard>
                    <Topbar>
                        <Title>Add New Employee</Title>
                        <CloseOutlinedIcon />
                    </Topbar>
                    <Form onSubmit={this.handleSubmit}>
                        <input type="text" onChange={this.handleChange}></input>
                    </Form>
                    <Buttons>
                        <button className="cancel">
                            Cancel
                        </button>
                        <button type="submit" className="create-new">
                            Create New Employee
                        </button>
                    </Buttons>
                </ModalCard>
            </Wrapper>
        )
    }
}

const mapStateToProps = state => ({
    data: state.data
})

const mapActionsToProps = {
    getEmployees
}

export default connect(mapStateToProps, mapActionsToProps)(Modal);
